/**
 * Copies the file located at filepath to destPath.
 * 
 * Note: On Android copyFile will overwrite destPath if it already exists. On iOS an error will be thrown if the file already exists.
 * @param {string} filepath - The file to copy
 * @param {"NativeFileDocuments.PathType.FullPath"|"NativeFileDocuments.PathType.DocumentsDirectory"} filepathType
 * @param {string} destPath - The destination to copy to
 * @param {"NativeFileDocuments.PathType.FullPath"|"NativeFileDocuments.PathType.DocumentsDirectory"} destPathType
 * @param {boolean} writeToLog
 * @returns {Promise.<void>}
 */
export async function copyFile(filepath, filepathType, destPath, destPathType, writeToLog) {
	// BEGIN USER CODE
	if (!filepath) {
		Promise.reject(new Error("No file path specified"));
	}
	if (!filepathType) {
		Promise.reject(new Error("No path type specified"));
	}

	if (!destPath) {
		Promise.reject(new Error("No destination path specified"));
	}
	if (!destPathType) {
		Promise.reject(new Error("No destination type specified"));
	}

	if (writeToLog) {
		NativeFileDocumentsUtils.writeToLog({
			actionName: "copyFile",
			logType: "Parameters",
			logMessage: JSON.stringify({
				filepath: filepath,
				filepathType: filepathType,
				destPath: destPath,
				destPathType: destPathType
			})
		});
	}

	const fullFilePath = NativeFileDocumentsUtils.getFullPath(filepath, filepathType, RNFS, Platform.OS);
	const fullDestPath = NativeFileDocumentsUtils.getFullPath(destPath, destPathType, RNFS, Platform.OS);

	if (writeToLog) {
		NativeFileDocumentsUtils.writeToLog({
			actionName: "copyFile",
			logType: "Info",
			logMessage: "Full file path: " + fullFilePath + ", full dest path: " + fullDestPath
		});
	}

	return RNFS.copyFile(fullFilePath, fullDestPath);

	// END USER CODE
}
